sys_script_include.script:
var GetEmailAddress = Class.create();
// entend the global.AbstractAjaxProcessor class
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//define the getEmail function
	getEmail: function(){
		gs.info("in sscrip include GetEmailAddress.getEmail!");
		var userRecord = new GlideRecord ("sys_user"); //create a GlideRecord for User table
		userRecord.get(this.getParameter('sysparm_userID')); //sysparm_indicates syntax to the Script Include that a valye will be passed in from a client-side script at runtime. Use getParameter() to retrieve paramter value
		gs.info("the email address got from db is: "+userRecord.email);
		return userRecord.email+'';	
	},
    type: 'GetEmailAddress'
});
