sys_script_include.script:
function validateEmailAddress(emailStr){

    //ensure emalStr is a String via JavaScript coercion
	emailStr=emailStr+'';
	gs.addInfoMessage("the email address was " +emailStr);
	//compare against the allowed syntax as specified in the regular expression
	if (emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	else {
		return false;
	}
 }
